import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import './App.css';
import {
  darkStars,
  darkMoon,
  darkClouds,
  darkCastle,
  lightSun,
  lightClouds,
  lightFlowers,
  lightHills
} from './assets';

const moods = ["happy", "sad", "angry", "calm", "curious", "bored"];
const moodEmojis = {
  happy: "üòä",
  sad: "üò¢",
  angry: "üò†",
  calm: "üòå",
  curious: "üßê",
  bored: "üòê"
};

function App() {
  const [selectedMood, setSelectedMood] = useState(null);
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [location, setLocation] = useState(null);
  const [error, setError] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const [showScroll, setShowScroll] = useState(false);

  // Scroll + Theme Class Handling
  useEffect(() => {
    const handleScroll = () => {
      setShowScroll(window.scrollY > 300);
    };

    window.addEventListener("scroll", handleScroll);
    document.body.classList.toggle("dark-mode", darkMode);
    document.body.classList.toggle("light-mode", !darkMode);

    return () => window.removeEventListener("scroll", handleScroll);
  }, [darkMode]);

  // Optional: Start with system theme
  useEffect(() => {
    const prefersDark = window.matchMedia("(prefers-color-scheme: dark)").matches;
    setDarkMode(prefersDark);
  }, []);

  const handleMoodClick = (mood) => {
    setSelectedMood(mood);
    setPlaces([]);
    setError(null);
    getLocationAndFetchPlaces(mood);
  };

  const getLocationAndFetchPlaces = (mood) => {
    if (!navigator.geolocation) {
      setError("Geolocation is not supported.");
      return;
    }

    setLoading(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const lat = position.coords.latitude;
        const lng = position.coords.longitude;
        setLocation({ lat, lng });

        fetch(`/get_places?lat=${lat}&lng=${lng}&keyword=${mood}`)
          .then((res) => res.json())
          .then((data) => {
            setPlaces(data.results || []);
          })
          .catch(() => {
            setError("Failed to fetch places.");
          })
          .finally(() => {
            setLoading(false);
          });
      },
      () => {
        setError("Location access denied.");
        setLoading(false);
      }
    );
  };
  const toggleDarkMode = () => {
  const curtain = document.getElementById("curtain");

  // Start curtain animation
  curtain.classList.add("animate");

  // Theme switch happens mid-animation
  setTimeout(() => {
    setDarkMode((prev) => !prev);
  }, 300);

  // Clean up animation after it's done
  setTimeout(() => {
    curtain.classList.remove("animate");
  }, 1000);
};

  return (
    <div className="page-wrapper">
      {darkMode ? (
  <>
    <img src="./assets/dark_stars.png" className="bg-prop dark-prop stars" alt="stars" />
    <img src="/dark_moon.png" className="bg-prop dark-prop moon" alt="moon" />
    <img src="./assets/dark_clouds.png" className="bg-prop dark-prop clouds" alt="clouds" />
    <img src="./assets/dark_castle.png" className="bg-prop dark-prop castle" alt="castle" />
  </>
) : (
  <>
    <img src="./assets/light_sun.png" className="bg-prop light-prop sun" alt="sun" />
    <img src="./assets/light_clouds.png" className="bg-prop light-prop clouds" alt="clouds" />
    <img src="./assets/light_flowers.png" className="bg-prop light-prop flowers" alt="flowers" />
    <img src="./assets/light_hills.png" className="bg-prop light-prop hills" alt="hills" />
  </>
)}

      <div className="app-container">
        <div style={{ textAlign: "right", marginBottom: "1rem" }}>
<div className="neon-toggle-wrapper">
  <label className="neon-switch">
<input
  type="checkbox"
  checked={darkMode}
  onChange={toggleDarkMode}

    />
    <span className="neon-slider"></span>
  </label>
</div>

        </div>

        <h1>How do you feel today?</h1>

        <div className="mood-buttons">
          {moods.map((mood) => (
            <button key={mood} onClick={() => handleMoodClick(mood)}>
              {moodEmojis[mood]} {mood}
            </button>
          ))}
        </div>

        {selectedMood && (
          <h2>You're feeling {moodEmojis[selectedMood]} <em>{selectedMood}</em></h2>
        )}

        {loading && (
          <div className="spinner-container">
            <div className="spinner"></div>
            <p>Finding places nearby...</p>
          </div>
        )}

        {error && <p className="error">{error}</p>}

        <div className="places-grid">
          {places.map((place, i) => (
            <div key={i} className="place-card">
              <h3>{place.name}</h3>
              {place.photos?.[0]?.photo_reference ? (
                <img
                  src={`https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=YOUR_GOOGLE_API_KEY`}
                  alt={place.name}
                />
              ) : (
                <div className="no-image">No image</div>
              )}
              {place.vicinity && <p>{place.vicinity}</p>}
              {place.rating && <p>‚≠ê {place.rating}</p>}
            </div>
          ))}
        </div>

        {location && (
          <div className="map-section">
            <h3>Map View</h3>
            <MapContainer
              className="leaflet-container"
              center={[location.lat, location.lng]}
              zoom={14}
              scrollWheelZoom={false}
            >
              <TileLayer
                attribution='&copy; OpenStreetMap'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              {places.map((place, i) => {
                const lat = place.geometry?.location?.lat;
                const lng = place.geometry?.location?.lng;
                return lat && lng ? (
                  <Marker key={i} position={[lat, lng]}>
                    <Popup>{place.name}</Popup>
                  </Marker>
                ) : null;
              })}
            </MapContainer>
          </div>
        )}
      </div>

      {showScroll && (
        <button
          className="back-to-top"
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
        >
          ‚¨Ü Back to Top
        </button>
      )}
      <div id="curtain" className="curtain-overlay"></div>
    </div>
  );
}

export default App;
