import React, { useState } from 'react';

const moods = ["happy", "sad", "angry", "calm", "curious", "bored"];

const moodEmojis = {
  happy: "ðŸ˜Š",
  sad: "ðŸ˜¢",
  angry: "ðŸ˜ ",
  calm: "ðŸ˜Œ",
  curious: "ðŸ§",
  bored: "ðŸ˜"
};


function App() {
  const [selectedMood, setSelectedMood] = useState(null);
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleMoodClick = (mood) => {
    setSelectedMood(mood);
    setPlaces([]);
    setError(null);
    getLocationAndFetchPlaces(mood);
  };

  const getLocationAndFetchPlaces = (mood) => {
    if (!navigator.geolocation) {
      setError("Geolocation is not supported by your browser.");
      return;
    }

    setLoading(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const lat = position.coords.latitude;
        const lng = position.coords.longitude;

        fetch(`/get_places?lat=${lat}&lng=${lng}&keyword=${mood}`)
          .then((res) => res.json())
          .then((data) => {
            if (data.results) {
              setPlaces(data.results);
            } else {
              setError("No results found.");
            }
          })
          .catch(() => {
            setError("Failed to fetch places.");
          })
          .finally(() => {
            setLoading(false);
          });
      },
      () => {
        setError("Unable to retrieve your location.");
        setLoading(false);
      }
    );
  };

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <h1>How do you feel today?</h1>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '1rem', flexWrap: 'wrap' }}>
        {moods.map((moodEmojis) => (
          <button key={moodEmojis} onClick={() => handleMoodClick(moodEmojis)}>
            {moodEmojis}
          </button>
        ))}
      </div>

      {selectedMood && <h2>You're feeling <em>{selectedMood}</em></h2>}

      {loading && <p>Loading places nearby...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {places.length > 0 && (
        <div>
          <h3>Suggested places for your mood:</h3>
          <ul>
            {places.map((place, i) => (
              <li key={i}>{place.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
